mapper.select:
SELECT * FROM tpm_user
WHERE 1 = 1
AND user_id = #{user_id}
AND tpm_id = #{tpm_id}
ORDER BY id DESC

mapper.update:
UPDATE
  tpm_user
SET
  qx = #{qx}
WHERE
  id = #{id}

mapper.select:
SELECT * FROM t_tpm
WHERE 1=1 AND tpm_name IS NOT NULL AND date_time IS NOT NULL AND tpm_is IS NOT NULL AND tpm_tq = 1 AND tpm_whbh = #{tpm_whbh}
order by id desc
limit #{offset},#{limit}

select count(*) from (


        select * from t_tpm a


                (a.tpm_name = #{item.tpm_name} and a.name = #{item.name})


        union
        select * from t_tpm a where a.tpm_name = #{user_name}


        select * from t_tpm a where a.tpm_name = #{user_name}


) a  where a.tpm_whbh = #{tpm_whbh}

select * from (


            select * from t_tpm a


                    (a.tpm_name = #{item.tpm_name} and a.name = #{item.name})


            union
            select * from t_tpm a where a.tpm_name = #{user_name}


            select * from t_tpm a where a.tpm_name = #{user_name}


) a where a.tpm_whbh = #{tpm_whbh}
limit #{offset},#{limit}

mapper.update:
UPDATE
  t_tpm
SET
  glz_user = #{glz_user}
WHERE
  glz_id = #{glz_id}
